						var elementBody = document.body;
						var elementLogo = document.querySelector('.Header img');
						var logoLight = 'https://1.bp.blogspot.com/-tzzvwhlM1w4/YD6KssSuu-I/AAAAAAAAdHI/RkzxkRearc415wH0yNxS_1beJrt8aYfjgCK4BGAYYCw/s280/webiumblack.png';
						var logoDark = 'https://1.bp.blogspot.com/-P0OSciKtuLI/YXW_g-cQwgI/AAAAAAAAlJE/bm8QS4Ylfug2czAUv2k1ULuUK1H_g_O2gCLcBGAsYHQ/output-onlinepngtools.png';

						
							// Darkmode
							var buttonColorMode = document.getElementById('button_color_mode');
							if (buttonColorMode) {
							buttonColorMode.addEventListener('click', function() {
							elementBody.classList.toggle('darkmode');
							localStorage.setItem('color_mode', 'dark' === localStorage.getItem('color_mode') ? 'light' : 'dark');
							if ('dark' === localStorage.getItem('color_mode')) {
							elementBody.classList.add('darkmode');

							if (elementLogo) {
							elementLogo.setAttribute('src', logoDark)
							}
							} else {
							elementBody.classList.remove('darkmode');
							
							if (elementLogo) {
							elementLogo.setAttribute('src', logoLight)
							}
							}
							})
							};

							if (localStorage.getItem('color_mode') === 'dark') {
							if (elementLogo) {
							elementLogo.setAttribute('src', logoDark)
							}
							} else {
							if (elementLogo) {
							elementLogo.setAttribute('src', logoLight)
							}
							};
						

						
							// Toggle Search
							var elementSearchInput = document.getElementById('search_input');
							var buttonToggleSearch = document.getElementById('button_toggle_search');
							if (buttonToggleSearch) {
							buttonToggleSearch.addEventListener('click', function() {
							elementBody.classList.toggle('search_active');
							elementSearchInput.focus()
							})
							};
