						var elementBody = document.body;

						
							// Darkmode
							var buttonColorMode = document.getElementById('button_color_mode');
							if (buttonColorMode) {
							buttonColorMode.addEventListener("click", function() {
							elementBody.classList.toggle("darkmode");
							localStorage.setItem("mode", "colormode" === localStorage.getItem("mode") ? "light" : "colormode");
							if ("colormode" === localStorage.getItem("mode")) {
							elementBody.classList.add("darkmode");
							} else {
							elementBody.classList.remove("darkmode");
							}
							})
							};
